mapper.insert:
INSERT INTO QNAQUESTION
(
	QSEQ
	,LECTURENUM
	,TITLE
	,CONTENTS
	,SREGINUM
	,COUNT
)
VALUES
(
	QSEQ.NEXTVAL
	,#{lectureNum}
	,#{title}
	,#{contents}
	,#{sregiNum}
	,0
)

	SELECT QSEQ.CURRVAL FROM DUAL

