INSERT INTO LECTURE_NOTICE
(
	LNSEQ
	,LECTURENUM
	,PREGINUM
	,TITLE
	,CONTENTS
)
VALUES
(
	LNSEQ.NEXTVAL
	,#{lectureNum}
	,#{pregiNum}
	,#{title}
	,#{contents}
)

	SELECT LNSEQ.CURRVAL FROM DUAL

mapper.select:
SELECT LNSEQ ,LECTURENUM ,PREGINUM ,TITLE ,CONTENTS ,INDATE FROM LECTURE_NOTICE WHERE LECTURENUM=#{lectureNum}
AND TITLE LIKE '%' || #{search} || '%'
ORDER BY INDATE DESC

mapper.update:
UPDATE 
	LECTURE_NOTICE
SET
	CONTENTS=#{contents}
WHERE
	LNSEQ=#{lnSeq}

