mapper.update:
UPDATE STUDENT
SET
	PASSWORD = #{arg1}
	UDATE = SYSDATE
WHERE
	SREGINUM = #{arg0}

UPDATE PROFESSOR
SET
	PASSWORD = #{arg1}
	UDATE = SYSDATE
WHERE
	PREGINUM = #{arg0}

	UPDATE PROFESSOR
SET
	 EMAIL = #{EMAIL}
	,ADDRESS = #{ADDRESS}
	,PHONENUM = #{TEL}
WHERE
	PREGINUM = #{PREGINUM}

	UPDATE MAJOR_PROFESSOR
SET
	 EMAIL = #{JEMAIL}
	,TEL = #{JTEL}
WHERE
	PREGINUM = #{PREGINUM}

mapper.select:
SELECT T.NOTICESEQ ,T.TITLE ,TO_CHAR(T.INDATE,'YYYY-MM-DD') AS INDATE FROM TOTNOTICE T
WHERE To_CHAR(T.INDATE,'YYYY') = #{value}
ORDER BY T.INDATE DESC
