mapper.update:
 update  
 	Applicant
 set
 	 	address=#{address}
	 	,birthday=to_date(#{birthday},'dd/mm/yyyy')
		,email=#{email}
		,majorSeq=#{majorSeq}
		,name=#{name}
		,password=#{password}
		,pic_name=#{pic_name}
		,requirement_file=#{requirement_file}
where
	phoneNum=#{phoneNum}
	and password=#{password}

 update  
 	Applicant
 set
 	 	address=#{address}
	 	,birthday=to_date(#{birthday},'dd/mm/yyyy')
		,email=#{email}
		,majorSeq=#{majorSeq}
		,name=#{name}
		,password=#{password}
		,pic_name=#{pic_name}
		,procedding = 'F'
		,requirement_file=#{requirement_file}
		,indate = sysdate
where
	phoneNum=#{phoneNum}
	and password=#{password}
